// 50 Value Stocks
const stocks = [
  'AAPL', 'MSFT', 'GOOGL', 'META', 'ORCL', 'IBM', 'CSCO', 'INTC',
  'JNJ', 'PFE', 'MRK', 'ABBV', 'BMY', 'AMGN', 'GILD', 'UNH',
  'PG', 'KO', 'PEP', 'WMT', 'COST', 'CL', 'KHC', 'GIS',
  'JPM', 'BAC', 'WFC', 'GS', 'MS', 'BLK', 'V', 'MA',
  'XOM', 'CVX', 'COP', 'VLO', 'MPC', 'PSX',
  'CAT', 'DE', 'HON', 'UNP', 'UPS', 'MMM', 'GE', 'RTX',
  'VZ', 'T', 'HD', 'LOW'
];

return stocks.map(s => ({ json: { symbol: s } }));

const items = $input.all();
const results = [];

for (const item of items) {
  const data = item.json;
  const p = Array.isArray(data) ? data[0] : data;

  if (!p || !p.symbol) continue;

  const marketCap = p.marketCap || 0;
  const price = p.price || 0;
  const lastDiv = p.lastDividend || 0;
  const divYield = price > 0 ? lastDiv / price : 0;

  // Format market cap display
  let mcDisplay = '$0';
  if (marketCap >= 1e12) mcDisplay = '$' + (marketCap/1e12).toFixed(1) + 'T';
  else if (marketCap >= 1e9) mcDisplay = '$' + (marketCap/1e9).toFixed(0) + 'B';
  else if (marketCap >= 1e6) mcDisplay = '$' + (marketCap/1e6).toFixed(0) + 'M';

  results.push({
    json: {
      symbol: p.symbol,
      company_name: p.companyName || '',
      sector: p.sector || '',
      industry: p.industry || '',
      exchange: p.exchange || '',
      price: Math.round(price * 100) / 100,
      market_cap: marketCap,
      market_cap_display: mcDisplay,
      pe_ratio: Math.round((p.pe || 0) * 100) / 100,
      beta: Math.round((p.beta || 1) * 100) / 100,
      dividend_yield: Math.round(divYield * 10000) / 10000,
      dividend_yield_pct: (divYield * 100).toFixed(2) + '%',
      volume: p.volume || 0,
      avg_volume: p.averageVolume || 0,
      year_high: p.range ? parseFloat(p.range.split('-')[1]) : 0,
      year_low: p.range ? parseFloat(p.range.split('-')[0]) : 0,
      change_pct: Math.round((p.changePercentage || 0) * 100) / 100,
      is_profitable: (p.pe || 0) > 0,
      is_dividend_paying: lastDiv > 0,
      is_penny_stock: price < 5,
      last_updated: new Date().toISOString().split('T')[0]
    }
  });
}

return results;
