const items = $input.all();
const results = [];

for (const item of items) {
  const s = item.json;
  if (!s.symbol) continue;

  const pe = parseFloat(s.pe_ratio) || 25;
  const beta = parseFloat(s.beta) || 1;
  const divYield = parseFloat(s.dividend_yield) || 0;
  const marketCap = parseFloat(s.market_cap) || 0;
  const price = parseFloat(s.price) || 0;
  const yearHigh = parseFloat(s.year_high) || price;
  const yearLow = parseFloat(s.year_low) || price;

  // ===== VALUATION SCORE (0-30) =====
  // Lower P/E is better (value)
  let score_valuation = 0;
  if (pe > 0 && pe < 100) {
    score_valuation = Math.max(0, Math.min(30, 35 - pe));
  }

  // ===== QUALITY SCORE (0-25) =====
  // Based on stability (lower beta) and profitability
  let score_quality = 15; // Base score
  if (beta < 0.8) score_quality += 10;
  else if (beta < 1.0) score_quality += 7;
  else if (beta < 1.2) score_quality += 4;
  else if (beta > 1.5) score_quality -= 5;
  if (pe < 0) score_quality -= 10; // Unprofitable
  score_quality = Math.max(0, Math.min(25, score_quality));

  // ===== GROWTH SCORE (0-20) =====
  // Based on price position in 52-week range
  let score_growth = 10; // Base
  const range = yearHigh - yearLow;
  if (range > 0) {
    const position = (price - yearLow) / range;
    if (position > 0.8) score_growth = 18; // Near highs = momentum
    else if (position > 0.6) score_growth = 14;
    else if (position < 0.3) score_growth = 6; // Near lows
  }
  score_growth = Math.max(0, Math.min(20, score_growth));

  // ===== DIVIDEND SCORE (0-15) =====
  let score_dividend = Math.min(15, divYield * 350);

  // ===== MOAT SCORE (0-10) =====
  // Based on market cap (larger = more established)
  let score_moat = 5;
  if (marketCap > 500e9) score_moat = 10;
  else if (marketCap > 100e9) score_moat = 8;
  else if (marketCap > 50e9) score_moat = 7;
  else if (marketCap < 10e9) score_moat = 4;

  // ===== TOTAL SCORE =====
  const total_score = Math.round(
    score_valuation + score_quality + score_growth + score_dividend + score_moat
  );

  // Classification
  let score_category = 'Poor';
  if (total_score >= 75) score_category = 'Excellent';
  else if (total_score >= 65) score_category = 'Good';
  else if (total_score >= 55) score_category = 'Fair';
  else if (total_score >= 45) score_category = 'Average';

  results.push({
    json: {
      symbol: s.symbol,
      company_name: s.company_name,
      sector: s.sector,
      industry: s.industry,
      exchange: s.exchange,
      price: price,
      market_cap: marketCap,
      market_cap_display: s.market_cap_display,
      pe_ratio: pe,
      beta: beta,
      dividend_yield: divYield,
      dividend_yield_pct: s.dividend_yield_pct,
      score_valuation: Math.round(score_valuation),
      score_quality: Math.round(score_quality),
      score_growth: Math.round(score_growth),
      score_dividend: Math.round(score_dividend),
      score_moat: Math.round(score_moat),
      total_score: total_score,
      score_category: score_category,
      rank: 0,
      is_profitable: s.is_profitable,
      is_dividend_paying: s.is_dividend_paying,
      is_penny_stock: s.is_penny_stock,
      year_high: yearHigh,
      year_low: yearLow,
      last_updated: new Date().toISOString().split('T')[0]
    }
  });
}

// Sort by total score descending and add rank
results.sort((a, b) => b.json.total_score - a.json.total_score);
results.forEach((r, i) => r.json.rank = i + 1);

return results;
