const items = $input.all();
const stage1Items = $('Read Stage 1').all();
const results = [];

for (let i = 0; i < items.length; i++) {
  const ratiosData = items[i].json;
  const stage1 = stage1Items[i]?.json || {};
  const ratios = Array.isArray(ratiosData) ? ratiosData[0] : ratiosData;

  if (!ratios || !stage1.symbol) continue;

  // Calculate scores
  const pe = ratios.peRatioTTM || 20;
  const roe = ratios.returnOnEquityTTM || 0;
  const debtEquity = ratios.debtEquityRatioTTM || 1;
  const divYield = ratios.dividendYieldTTM || 0;

  // Valuation Score (0-30)
  const valScore = Math.max(0, Math.min(30, 30 - (pe - 5) * 1.5));

  // Quality Score (0-25)
  const qualScore = Math.min(25, Math.abs(roe) * 100);

  // Dividend Score (0-15)
  const divScore = Math.min(15, divYield * 300);

  // Total Score
  const totalScore = Math.round(valScore + qualScore + divScore);

  // Classification
  let classification = 'Average';
  if (totalScore >= 60) classification = 'Excellent';
  else if (totalScore >= 50) classification = 'Good';
  else if (totalScore >= 40) classification = 'Fair';

  results.push({
    json: {
      rank: i + 1,
      symbol: stage1.symbol,
      companyName: stage1.companyName,
      sector: stage1.sector,
      totalScore: totalScore,
      classification: classification,
      valuationScore: Math.round(valScore),
      qualityScore: Math.round(qualScore),
      dividendScore: Math.round(divScore),
      peRatio: Math.round(pe * 100) / 100,
      roe: Math.round(roe * 10000) / 100,
      debtEquity: Math.round(debtEquity * 100) / 100,
      dividendYield: Math.round(divYield * 10000) / 100,
      scoringDate: new Date().toISOString().split('T')[0]
    }
  });
}

// Sort by score
results.sort((a, b) => b.json.totalScore - a.json.totalScore);
results.forEach((r, i) => r.json.rank = i + 1);

return results;
